/*
*/
/**/

/*1*/inline byteT reverseBitsF( const byteT bP )/*1*/
{
    return (byteT)( ( ( bP * 0x0802LU & 0x22110LU ) | ( bP * 0x8020LU & 0x88440LU ) ) * 0x10101LU >> 16 ) ;
}

//SOURCE: c:\ideafarm.home.1\precious\domains\com\ideafarm\city\library\dictionary\1snip.17*.* : 1snip.1700011a.reversebitsf END
//SOURCE: c:\ideafarm.home.1\precious\domains\com\ideafarm\city\library\dictionary\1snip.17*.* : 1snip.1700011b.reversebitsinfieldf BEGIN

/*
*/
/**/

/*1*/inline byteT reverseBitsInFieldF( const byteT bP , const countT offBitP , const countT cBitsP )/*1*/
{
    byteT was = (byteT)( bP >> offBitP                      ) ;       //was: cBitsP          BITS OF INTEREST ARE AT OFFSET 0
    byteT now = (byteT)( reverseBitsF( was ) >> SB - cBitsP ) ;       //now: cBitsP REVERSED BITS OF INTEREST ARE AT OFFSET 0 AND REMAINING BITS ARE 0
    now <<= offBitP ;                                                 //now: cBitsP REVERSED BITS OF INTEREST ARE AT OFFSET offBitP

    if( offBitP )
    {
        byteT prefix = bP ;
        prefix <<= SB - offBitP ;
        prefix >>= SB - offBitP ;

        now |= prefix ;
    }

    const countT cBitsSuffix = SB - offBitP - cBitsP ;
    if( cBitsSuffix )
    {
        byteT suffix = bP ;
        suffix >>= SB - cBitsSuffix ;
        suffix <<= SB - cBitsSuffix ;

        now |= suffix ;
    }

    return now ;
}

