/**/

/*1*/inline countT idaF( countT idrP , countT id1P , countT cP )/*1*/
{
    if( !idrP || cP < idrP ) return 0 ;
    else
    {
        countT ida = id1P - 1 + idrP ;
        if( ida > cP ) return ida - cP ;  //FASTER THAN MODULO
        else           return ida      ;
    }
}

